import React, { Component } from 'react'

import { ScheduleComponent, Inject, Day,Week,WorkWeek,Month,Agenda,Resize,DragAndDrop,ViewsDirective,ViewDirective,ExcelExport } from '@syncfusion/ej2-react-schedule';
//import { SampleBase } from '../common/sample-base';
import { DataManager, WebApiAdaptor ,ODataV4Adaptor } from '@syncfusion/ej2-data';
import { L10n } from '@syncfusion/ej2-base';

L10n.load({
  'en-US': {
      'schedule': {
          'saveButton': 'Save',
          'cancelButton': 'Close',
          'deleteButton': 'Remove',
          'newEvent': 'Add Class',
      },
  }
});
export class ScheduleComponent1 extends Component {
    constructor() {
        super(...arguments);
        this.dataManager = new DataManager({
          url: 'https://js.syncfusion.com/demos/ejservices/api/Schedule/LoadData',
          adaptor: new ODataV4Adaptor
      });
    }
    onActionBegin(args) {
      if (args.requestType === 'toolbarItemRendering') {

          let exportIt = {
            align: 'Right', showTextOn: 'Both', prefixIcon: 'e-icon-schedule-excel-export',
            text: ' Export', cssClass: 'e-excel-export', click: this.onExportDataClick.bind(this)
        };
        let exportItem = {
          align: 'Right', showTextOn: 'Both', prefixIcon: 'e-icon-schedule-excel-export',
          text: 'Excel Export', cssClass: 'e-excel-export', click: this.onExportClick.bind(this)
      };
          args.items.push(exportItem);
          args.items.push(exportIt);
      }
    }
    onRenderCell(date) {
        //console.log('render')
    }
    change(args) {
        this.scheduleObj.selectedDate = args.value;
        this.scheduleObj.dataBind();
    }
    onDragStart(args) {
        args.navigation.enable = true;
    }
    onExportClick() {
      let exportValues = {
        fields: ['Id', 'Subject', 'StartTime', 'EndTime', 'Location','Description','RecurrenceRule']
    };
      this.scheduleObj.exportToExcel(exportValues)
      console.log(this.scheduleObj.eventsData)



    }
    onExportDataClick(){
      //const svalues = this.scheduleObj.eventsData;
      this.items = this.scheduleObj.eventsData.map((item, key) =>
      console.log(item.Id+item.EndTime,item.Guid))
      console.log(this.scheduleObj.eventsData)
      // let jsondata = JSON.stringify(this.scheduleObj.eventsData);
      // this.fs.writeFileSync('jsonfile', jsondata);


    }
    render() {
        return (<div className='schedule-control-section'>
        <div className='col-lg-9 control-section'>
          <div className='control-wrapper'>
            <ScheduleComponent height='650px' cssClass='excel-export' width='100%' 
            id='schedule' ref={schedule => this.scheduleObj = schedule} 
            selectedDate={new Date(2017,3,15)}showWeekNumber={true} eventSettings={{ dataSource: this.dataManager, }} 
            actionBegin={this.onActionBegin.bind(this)} dragStart={(this.onDragStart.bind(this))}
            renderCell={this.onRenderCell.bind(this)}
            >
              <ViewsDirective>
                <ViewDirective option='Day' startHour='09:00' endHour = '19:00'/>
                <ViewDirective option='Week' startHour='09:00' endHour = '19:00'/>
                <ViewDirective option='WorkWeek'/>
                <ViewDirective option='Month'/>
              </ViewsDirective>
              <Inject services={[Day, Week, WorkWeek, Month, Agenda, Resize, DragAndDrop,ExcelExport]}/>
            </ScheduleComponent>
          </div>
        </div>
        <div className='col-lg-3 property-section'>

        </div>
      </div>);
    }
}

export default ScheduleComponent1
