import React, { Component } from 'react'
import { ScheduleComponent, ViewsDirective, ViewDirective, Day, Week, WorkWeek, Month, Agenda, Inject, Resize, DragAndDrop } from '@syncfusion/ej2-react-schedule';
import { extend } from '@syncfusion/ej2-base';
//import { DatePickerComponent } from '@syncfusion/ej2-react-calendars';
import * as dataSource from '../datasource.json';
export class ViewComponent extends React.Component {
  constructor() {
    super(...arguments);
    this.data = extend([], dataSource.scheduleData, null, true);
}
change(args) {
    this.scheduleObj.selectedDate = args.value;
    this.scheduleObj.dataBind();
}
onDragStart(args) {
    args.navigation.enable = true;
}
render() {
  return (<div className='schedule-control-section'>
  <div className='col-lg-9 control-section'>
    <div className='control-wrapper'>
      <ScheduleComponent height='650px' ref={schedule => this.scheduleObj = schedule} selectedDate={new Date(2019, 0, 10)} readonly = {true} eventSettings={{ dataSource: this.data }} dragStart={(this.onDragStart.bind(this))}>
        <ViewsDirective>
          <ViewDirective option='Day' startHour='09:00' endHour = '20:00'/>
          <ViewDirective option='Week' startHour='09:00' endHour = '20:00'/>
          <ViewDirective option='WorkWeek'/>
          <ViewDirective option='Month'/>

        </ViewsDirective>
        <Inject services={[Day, Week, WorkWeek, Month, Agenda, Resize, DragAndDrop]}/>
      </ScheduleComponent>
    </div>
  </div>

</div>);
}
}
