import React from 'react';
import MaterialTable from 'material-table';

export default function MaterialTableDemo() {
  const [state, setState] = React.useState({
    columns: [
      { title: 'Name', field: 'name' },
      { title: 'Stuff Id', field: 'stuffid' },
      { title: 'email', field: 'email' },
      {
        title: 'Section ',
        field: 'group',
        lookup: { 34: 'class 2', 63: 'class 4' },
      },
    ],
    data: [
      { name: 'Mehmet', stuffid: 'k1234567', email: 'a@athuman', group: 63 },
      {
        name: 'Zerya Betül',
        stuffid: 'k123456',
        email: 'b@athuman',
        group: 34,
      },
    ],
  });

  return (
    <MaterialTable
      title="Editable Example"
      columns={state.columns}
      data={state.data}
      editable={{
        onRowAdd: newData =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              const data = [...state.data];
              data.push(newData);
              setState({ ...state, data });
            }, 600);
          }),
        onRowUpdate: (newData, oldData) =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              const data = [...state.data];

              let updateAt = -1
                 data.forEach((element, index) => {
                   console.log(element, oldData)
                if(JSON.stringify(element) === JSON.stringify(oldData,["name", "stuffid", "email", "group"])){
                  updateAt = index
                }})
                console.log(updateAt)

              data[updateAt] = newData;
              setState({ ...state, data });
            }, 600);
          })

      }}
    />
  );
}
